import { useState, useEffect } from "react";
import SockJS from "sockjs-client";
import StompJS from "@stomp/stompjs";
import axios from "axios";
import "./test.css";

const Test = () => {
  const [text, setText] = useState("");
  const [messages, setMessages] = useState([]);
  const [currentRoomId, setCurrentRoomId] = useState(0);
  const URL = "";

  const initialChatSetting = async () => {
    await axios.get(URL).then((res) => setMessages(res.data));
  };

  const setWebSocket = async () => {
    try {
      const sockjs = new SockJS(``);
      const ws = StompJS.over(sockjs);
    } catch (error) {
      console.log(error);
    }
  };

  const inputHandler = (e) => {
    setText(e.target.value);
  };

  // useEffect(() => {
  //   if (curruntRoomId === 0) return;
  //   initialChatSetting();
  //   setWebSocket();
  // }, [curruntRoomId]);

  return (
    <div className="container">
      <div className="chat_container">
        <div className="chat">
          {messages.map((el) => {
            return <div>{el}</div>;
          })}
        </div>
        <div className="input_container">
          <input onChange={inputHandler} value={text} />
          <button>send</button>
        </div>
      </div>
      <div className="setRoom_container">
        <input onChange={inputHandler} value={text} />
        <button>setRoom</button>
      </div>
    </div>
  );
};

export default Test;
