import { useState, useEffect } from "react";
import SockJS from "sockjs-client";
// import StompJS from "stompjs";
import * as StompJs from "@stomp/stompjs";
import axios from "axios";
import "./test.css";

const Test = () => {
  const [text, setText] = useState("");
  const [messages, setMessages] = useState([]);
  const [currentRoomId, setCurrentRoomId] = useState(0);
  const [createRoomId, setCreateRoomId] = useState("");
  const [changeRoomId, setChangeRoomId] = useState("");
  const [profileId, setProfileId] = useState("");
  const URL = "https://api-tutordiff.site";

  const initialChatSetting = async () => {
    await axios
      .get(`${URL}/messages/rooms/{profileId}/${currentRoomId}`)
      .then((res) => setMessages(res.data));
  };

  const setWebSocket = async () => {
    try {
      const sockjs = new SockJS(``);
      const ws = StompJs.Client(sockjs);
      await ws.connect("TOKEN", () => {
        ws.subscribe(``, async (msg) => {
          setMessages([...messages, JSON.parse(msg.body)]);
          //구독한 location에서 메시지를 수신했을 때 처리해주고자 하는 동작
        });
      });
    } catch (error) {
      console.log(error);
    }
  };

  const inputHandler = (e) => {
    setText(e.target.value);
  };

  // useEffect(() => {
  //   if (curruntRoomId === 0) return;
  //   initialChatSetting();
  //   setWebSocket();
  // }, [curruntRoomId]);

  return (
    <div className="container">
      <div className="chat_container">
        <div className="chat">
          {messages.map((el) => {
            return <div>{el}</div>;
          })}
        </div>
        <div className="input_container">
          <input onChange={inputHandler} value={text} />
          <button>send</button>
        </div>
      </div>
      <div className="setRoom_container">
        <input
          onChange={(e) => setChangeRoomId(e.target.value)}
          value={changeRoomId}
        />
        <button onClick={() => setCurrentRoomId(Number(changeRoomId))}>
          setRoom
        </button>
      </div>
      <div className="createRoom_container">
        <button
          onClick={() => {
            axios
              .post("/room", {
                receiverId: 1,
                senderId: 2,
              })
              .then(({ data }) => setCreateRoomId(data.roomId));
          }}
        >
          createRoom
        </button>
        <>{createRoomId}</>
      </div>
    </div>
  );
};

export default Test;
